properties.search_CSS:
.searchmsf__wrapper {
  width: 100%;
  padding: 10px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: red;
  position: relative
}
.searchmsf__input {
  outline: none;
  border: none;
  box-sizing: border-box;
  width: 350px;
  height:40px;
  font-size: 16px;
  padding: 0 10px
}
.searchmsf__dropdown {
  box-sizing: border-box;
  position: relative;
  background-color: white;
  width: 150px
}
.searchmsf__dropdown_header {
  box-sizing: border-box;
  width: 150px;
  height: 40px;
  padding: 0 10px;
  background-color: bisque;
  display: flex;
  align-items: center;
  cursor: pointer
}
.searchmsf__dropdown_header_icon {
  width: 20px;
  height: 20px;
  font-size: 20px;
  margin-right: 20px
}
.searchmsf__dropdown_optionblock {
  position: absolute;
  top:40px;
  left:0;
  background-color: orange;
  width: 150px;
  z-index: 100 !important
}
.searchmsf__dropdown_optionblock ul {
list-style: none;
  margin:0;
  padding: 0
}
  .searchmsf__dropdown_option {
  background-color: rgb(253, 248, 186);
  border: none;
  height: 40px;
  width: 150px;
  padding: 0 10px;
  display: flex;
  align-items: center;
  position: relative;
  cursor: pointer;
  transition: 0.2s
}
.searchmsf__dropdown_option:hover {
  background-color: rgb(253, 192, 77)
}
.searchmsf__dropdown_option_icon {
  width: 20px;
  height: 20px;
  font-size: 20px;
  margin-right: 20px
}
.searchmsf__button {
  border: none;
  cursor: pointer;
  height: 40px;
  margin-left: 10px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: rgba(0,0,0,0)
}
.searchmsf__icon {
  width: 30px;
  height: 30px;
  font-size: 30px;
  color: white;
  transition: 0.3s
}
.searchmsf__icon:hover {
  transform: rotate(-30deg)
}
